// src/components/ParticlesBackground/ParticlesBackground.jsx
import { useCallback } from "react";
import Particles from "react-tsparticles";
// Usaremos loadFull para asegurarnos de que el motor se inicialice completamente
import { loadFull } from "tsparticles"; 

const ParticlesBackground = () => {

    // Función de inicialización CLAVE: Asegura que el motor esté listo
    const particlesInit = useCallback(async (engine) => {
        // Carga el motor completo de tsParticles
        await loadFull(engine);
    }, []);

    const particlesLoaded = useCallback(async (container) => {
        console.log("Partículas cargadas exitosamente!");
    }, []);

    // Opciones para el efecto de "Polvo Oscuro"
    const options = {
        background: {
            color: {
                // Fondo oscuro deseado
                value: "#000000", 
            },
        },
        particles: {
            number: {
                value: 80, // Número de partículas
                density: { enable: true, value_area: 800 },
            },
            color: {
                // Partículas blancas para contraste
                value: "#f2f2f2", 
            },
            opacity: {
                value: 0.5,
                random: true,
            },
            size: {
                value: 1.5,
            },
            move: {
                enable: true,
                speed: 0.3, // Movimiento sutil
                direction: "none",
                random: true,
                straight: false,
                out_mode: "out",
            },
            links: {
                enable: false, // Quitamos las líneas entre partículas
            }
        },
        interactivity: {
            events: {
                onHover: { enable: false },
                onClick: { enable: false },
            },
        },
    };

    return (
        <Particles
            id="tsparticles"
            init={particlesInit} // Usamos la nueva función de inicialización
            loaded={particlesLoaded}
            options={options}
            style={{
                position: "fixed", 
                top: 0,
                left: 0,
                width: "100%",
                height: "100%",
                zIndex: -1, // Detrás de todo el contenido
            }}
        />
    );
};

export default ParticlesBackground;
