// src/components/ProtectedRoute.jsx
import React from 'react';
import { Navigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext'; // Importamos el estado de autenticación

const ProtectedRoute = ({ children }) => {
  const { isLoggedIn } = useAuth();

  if (!isLoggedIn) {
    // Si el usuario NO está logeado, lo redirigimos a la página de acceso/login
    return <Navigate to="/acceso" replace />;
  }

  // Si el usuario SÍ está logeado, renderizamos los componentes hijos (la página solicitada)
  return children;
};

export default ProtectedRoute;
