// src/pages/Home.jsx
import React from 'react';

const Home = () => {
  return (
    // minHeight: 200vh asegura que la página sea larga y fuerce el scroll
    <div className="home-page-container" style={{ minHeight: '200vh', paddingTop: '100px', backgroundColor: '#f9f9f9' }}>
      <section style={{ maxWidth: '800px', margin: '0 auto', padding: '40px 20px', textAlign: 'center' }}>
        <h2>Bienvenido a Mascotas: Belleza y Bienestar</h2>
        <p style={{ fontSize: '1.1rem', color: '#555' }}>
          La plataforma minimalista para gestionar la estética y salud de tu mejor amigo. 
          Desliza hacia abajo para ver el efecto de cápsula de la navegación.
        </p>
      </section>

      {/* Bloque de Contenido 1 (Largo para scroll) */}
      <section style={{ height: '500px', backgroundColor: '#fff', margin: '40px 0', boxShadow: '0 2px 10px rgba(0,0,0,0.05)', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
        <h3>Nuestros Servicios Destacados (Sección de Relleno)</h3>
      </section>

      {/* Bloque de Contenido 2 (Asegura la longitud total) */}
      <section style={{ height: '500px', margin: '40px 0', display: 'flex', alignItems: 'center', justifyContent: 'center', flexDirection: 'column' }}>
        <h3>Agenda tu Cita Hoy</h3>
        <p>¡Aquí irá el componente de agendamiento!</p>
      </section>

      {/* Relleno final para asegurar el scroll total */}
      <div style={{ height: '300px' }}></div> 
    </div>
  );
};

export default Home;
