// src/components/Register/Register.jsx - Implementación de Registro Multi-Paso
import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './Register.css'; 
import { useAuth } from '../../contexts/AuthContext'; 

// Lista de razas comunes para simplificar la interfaz (esto podría venir de una API real)
const commonBreeds = [
    'Labrador Retriever', 'German Shepherd', 'Poodle', 'Bulldog', 'Beagle', 'Chihuahua', 'Mestizo/Cruzado'
];

const Register = () => {
    const [step, setStep] = useState(1); // Control del paso actual (1 o 2)
    const [clientData, setClientData] = useState({
        name: '',
        phone: '', // ¡Nuevo campo de contacto!
        email: '',
        password: '',
        confirmPassword: ''
    });
    const [petData, setPetData] = useState({
        petName: '',
        species: 'Perro', // Por defecto
        breed: '',
        age: '',
        weight: '',
        notes: '' // Notas de temperamento o salud
    });
    const [error, setError] = useState(''); 

    const { register } = useAuth();
    const navigate = useNavigate();

    // --- Manejadores de Cambio ---
    const handleClientChange = (e) => {
        setClientData({ ...clientData, [e.target.id]: e.target.value });
    };

    const handlePetChange = (e) => {
        setPetData({ ...petData, [e.target.id]: e.target.value });
    };

    // --- Lógica de Pasos y Validación ---
    const handleNext = (e) => {
        e.preventDefault();
        setError('');

        if (step === 1) {
            if (clientData.password !== clientData.confirmPassword) {
                setError("Las contraseñas no coinciden.");
                return;
            }
            // Validación mínima de campos
            if (!clientData.name || !clientData.email || !clientData.phone) {
                 setError("Por favor, completa todos los campos de contacto.");
                 return;
            }
            setStep(2); // Avanzar al paso de la mascota
        }
    };

    const handleBack = () => {
        setStep(1);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');

        // Validación final del Paso 2
        if (!petData.petName || !petData.breed || !petData.age || !petData.weight) {
            setError('Por favor, completa todos los datos obligatorios de tu mascota.');
            return;
        }

        try {
            // 1. Llamada al registro principal (solo dueño)
            const success = register(clientData.name, clientData.email, clientData.password, clientData.phone); 

            if (success) {
                // 2. Aquí, en un sistema real, enviarías los petData 
                //    a una API separada para asociarlos al nuevo usuario.
                console.log("Datos de la Mascota a enviar a la API:", petData);

                // Redirigir al Home tras el registro exitoso
                navigate('/');
            } else {
                setError('Error en el registro de la cuenta. Intenta con un email diferente.');
            }
        } catch (err) {
            setError('Ocurrió un error al intentar registrar la cuenta y la mascota.');
            console.error(err);
        }
    };

    // ----------------------------------------------------
    // VISTAS POR PASO
    // ----------------------------------------------------

    const renderStep1Owner = () => (
        <form onSubmit={handleNext}>
            <h3>Paso 1: Datos del Dueño y Contacto</h3>

            <div className="input-group">
                <label htmlFor="name">Nombre Completo</label>
                <input
                    type="text"
                    id="name"
                    value={clientData.name}
                    onChange={handleClientChange}
                    required
                />
            </div>

            <div className="input-group">
                <label htmlFor="phone">Teléfono de Contacto</label>
                <input
                    type="tel"
                    id="phone"
                    value={clientData.phone}
                    onChange={handleClientChange}
                    placeholder="Ej: 555-1234-567"
                    required
                />
            </div>

            <div className="input-group">
                <label htmlFor="email">Email</label>
                <input
                    type="email"
                    id="email"
                    value={clientData.email}
                    onChange={handleClientChange}
                    required
                />
            </div>

            <div className="input-group">
                <label htmlFor="password">Contraseña</label>
                <input
                    type="password"
                    id="password"
                    value={clientData.password}
                    onChange={handleClientChange}
                    required
                />
            </div>

            <div className="input-group">
                <label htmlFor="confirmPassword">Confirmar Contraseña</label>
                <input
                    type="password"
                    id="confirmPassword"
                    value={clientData.confirmPassword}
                    onChange={handleClientChange}
                    required
                />
            </div>

            <button type="submit" className="register-button primary">
                Continuar a Datos de Mascota
            </button>
        </form>
    );

    const renderStep2Pet = () => (
        <form onSubmit={handleSubmit}>
            <h3>Paso 2: Información de la Mascota</h3>

            <div className="input-group">
                <label htmlFor="petName">Nombre de la Mascota</label>
                <input
                    type="text"
                    id="petName"
                    value={petData.petName}
                    onChange={handlePetChange}
                    required
                />
            </div>

            <div className="input-group">
                <label htmlFor="species">Especie</label>
                <select id="species" value={petData.species} onChange={handlePetChange} required>
                    <option value="Perro">Perro</option>
                    <option value="Gato">Gato</option>
                    <option value="Otro">Otro</option>
                </select>
            </div>

            <div className="input-group">
                <label htmlFor="breed">Raza</label>
                <select id="breed" value={petData.breed} onChange={handlePetChange} required>
                    <option value="">Selecciona una raza</option>
                    {commonBreeds.map(breed => (
                        <option key={breed} value={breed}>{breed}</option>
                    ))}
                    <option value="Otro">Otra raza (Especificar en notas)</option>
                </select>
            </div>

            <div className="row-group"> {/* Para Edad y Peso juntos */}
                <div className="input-group">
                    <label htmlFor="age">Edad (Años)</label>
                    <input
                        type="number"
                        id="age"
                        value={petData.age}
                        onChange={handlePetChange}
                        min="0"
                        max="30"
                        required
                    />
                </div>
                <div className="input-group">
                    <label htmlFor="weight">Peso (Kg)</label>
                    <input
                        type="number"
                        id="weight"
                        value={petData.weight}
                        onChange={handlePetChange}
                        min="0"
                        step="0.1"
                        required
                    />
                </div>
            </div>

            <div className="input-group">
                <label htmlFor="notes">Notas Importantes (Temperamento, Alergias, etc.)</label>
                <textarea
                    id="notes"
                    value={petData.notes}
                    onChange={handlePetChange}
                    rows="3"
                    placeholder="Ej: Es sensible al ruido o requiere medicamento especial."
                ></textarea>
            </div>


            <div className="register-actions">
                <button type="button" onClick={handleBack} className="register-button secondary">
                    Volver
                </button>
                <button type="submit" className="register-button primary">
                    Completar Registro
                </button>
            </div>
        </form>
    );


    return (
        <div className="register-container">
            <div className="register-card">
                <h2>Crear Cuenta</h2>

                {/* Indicador de Pasos (Stepper) */}
                <div className="step-indicator">
                    <span className={`step ${step === 1 ? 'active' : ''}`}>1. Dueño y Contacto</span>
                    <span className={`step ${step === 2 ? 'active' : ''}`}>2. Mascota</span>
                </div>

                {/* Mostrar mensaje de error */}
                {error && <p className="error-message">{error}</p>}

                {/* Renderizar el paso actual */}
                {step === 1 && renderStep1Owner()}
                {step === 2 && renderStep2Pet()}

                <div className="register-footer">
                    <p>
                        ¿Ya tienes una cuenta? <Link to="/acceso">Inicia Sesión</Link>
                    </p>
                </div>
            </div>
        </div>
    );
};

export default Register;
