// src/pages/admin/AdminDashboard.jsx
import React, { useState } from 'react';
import { useData } from '../../contexts/DataContext';
import { useAuth } from '../../contexts/AuthContext';
import { 
    FaCut, FaShoppingBag, FaPaw, FaChevronLeft, FaChevronRight, 
    FaPlusCircle, FaSignOutAlt, FaUserShield 
} from 'react-icons/fa';
import './AdminDashboard.css';

const AdminDashboard = () => {
    const { services, products, pets, addService, addProduct } = useData();
    const { logout, user } = useAuth();
    const [tab, setTab] = useState('servicios');
    const [isSidebarOpen, setIsSidebarOpen] = useState(true);

    const [serviceForm, setServiceForm] = useState({ title: '', description: '', price: '', duration: '', category: 'Estética' });
    const [productForm, setProductForm] = useState({ name: '', price: '', stock: '', category: 'Alimentos' });

    const toggleSidebar = () => setIsSidebarOpen(!isSidebarOpen);

    const handleServiceSave = (e) => {
        e.preventDefault();
        addService(serviceForm);
        setServiceForm({ title: '', description: '', price: '', duration: '', category: 'Estética' });
    };

    const handleProductSave = (e) => {
        e.preventDefault();
        addProduct(productForm);
        setProductForm({ name: '', price: '', stock: '', category: 'Alimentos' });
    };

    return (
        <div className={`admin-layout ${isSidebarOpen ? 'sidebar-open' : 'sidebar-closed'}`}>

            {/* BARRA SUPERIOR FIJA */}
            <header className="admin-top-bar">
                <div className="admin-user-info">
                    <FaUserShield />
                    <span>Admin: <strong>{user?.name}</strong></span>
                </div>
                <button className="logout-pill" onClick={logout}>
                    <FaSignOutAlt /> <span>Cerrar Sesión</span>
                </button>
            </header>

            {/* MENU LATERAL */}
            <aside className="admin-sidebar">
                <button className="toggle-btn" onClick={toggleSidebar}>
                    {isSidebarOpen ? <FaChevronLeft /> : <FaChevronRight />}
                </button>

                <div className="sidebar-header">
                    {isSidebarOpen && <h3>VetControl Pro</h3>}
                </div>

                <nav className="sidebar-nav">
                    <button onClick={() => setTab('servicios')} className={`nav-capsule ${tab === 'servicios' ? 'active' : ''}`}>
                        <FaCut /> {isSidebarOpen && <span>Servicios</span>}
                    </button>
                    <button onClick={() => setTab('productos')} className={`nav-capsule ${tab === 'productos' ? 'active' : ''}`}>
                        <FaShoppingBag /> {isSidebarOpen && <span>Productos</span>}
                    </button>
                    <button onClick={() => setTab('pacientes')} className={`nav-capsule ${tab === 'pacientes' ? 'active' : ''}`}>
                        <FaPaw /> {isSidebarOpen && <span>Pacientes</span>}
                    </button>
                </nav>
            </aside>

            {/* AREA DE TRABAJO */}
            <main className="admin-main-panel">
                <header className="content-header">
                    <h2>Gestión de {tab.charAt(0).toUpperCase() + tab.slice(1)}</h2>
                </header>

                <div className="content-body">
                    {tab === 'servicios' && (
                        <div className="fade-in">
                            <form onSubmit={handleServiceSave} className="dashboard-form">
                                <div className="form-header"><FaPlusCircle /> <h4>Nuevo Servicio</h4></div>
                                <div className="input-grid">
                                    <input type="text" placeholder="Título" value={serviceForm.title} onChange={e => setServiceForm({...serviceForm, title: e.target.value})} required />
                                    <input type="number" placeholder="Precio ($)" value={serviceForm.price} onChange={e => setServiceForm({...serviceForm, price: e.target.value})} required />
                                    <input type="text" placeholder="Duración (Ej: 1h)" value={serviceForm.duration} onChange={e => setServiceForm({...serviceForm, duration: e.target.value})} required />
                                    <textarea placeholder="Descripción" value={serviceForm.description} onChange={e => setServiceForm({...serviceForm, description: e.target.value})} required />
                                </div>
                                <button type="submit" className="main-action-btn">Guardar Servicio</button>
                            </form>
                            <div className="inventory-preview">
                                <h4>Servicios Activos ({services.length})</h4>
                                <div className="scroll-list">
                                    {services.map(s => <div key={s.id} className="list-card">{s.title} <span>${s.price}</span></div>)}
                                </div>
                            </div>
                        </div>
                    )}

                    {tab === 'productos' && (
                        <div className="fade-in">
                            <form onSubmit={handleProductSave} className="dashboard-form">
                                <div className="form-header"><FaPlusCircle /> <h4>Nuevo Producto</h4></div>
                                <div className="input-grid">
                                    <input type="text" placeholder="Nombre" value={productForm.name} onChange={e => setProductForm({...productForm, name: e.target.value})} required />
                                    <input type="number" placeholder="Precio" value={productForm.price} onChange={e => setProductForm({...productForm, price: e.target.value})} required />
                                    <input type="number" placeholder="Stock" value={productForm.stock} onChange={e => setProductForm({...productForm, stock: e.target.value})} required />
                                </div>
                                <button type="submit" className="main-action-btn">Registrar Producto</button>
                            </form>
                        </div>
                    )}

                    {tab === 'pacientes' && (
                        <div className="patient-gallery fade-in">
                            {pets.length > 0 ? pets.map(p => (
                                <div key={p.id} className="pet-capsule-card">
                                    <div className="pet-icon"><FaPaw /></div>
                                    <div className="pet-info">
                                        <h4>{p.petName}</h4>
                                        <p>{p.breed} • {p.weight}kg</p>
                                    </div>
                                </div>
                            )) : <p>No hay pacientes registrados.</p>}
                        </div>
                    )}
                </div>
            </main>
        </div>
    );
};

export default AdminDashboard;
