// src/App.js
import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import './App.css'; 

import { AuthProvider } from './contexts/AuthContext'; 
import { DataProvider } from './contexts/DataContext'; // <-- Nuevo!
import Navbar from './components/Navbar/Navbar'; 
import Footer from './components/Footer/Footer'; 
import WhatsAppButton from './components/WhatsAppButton/WhatsAppButton';
import ProtectedRoute from './components/ProtectedRoute'; 

import Home from './pages/Home';
import Services from './pages/Services'; 
import Login from './components/Login/Login';
import Register from './components/Register/Register';
import AdminDashboard from './pages/AdminDashboard/AdminDashboard'; // <-- Nuevo!

function App() {
  return (
    <AuthProvider>
      <DataProvider> {/* Envuelve para que los datos fluyan */}
        <Router>
            <div className="app-container">
                <Navbar />
                <main className="main-content">
                    <Routes>
                        <Route path="/" element={<ProtectedRoute><Home /></ProtectedRoute>} />
                        <Route path="/acceso" element={<Login />} />
                        <Route path="/registro" element={<Register />} />

                        <Route path="/servicios" element={
                            <ProtectedRoute allowedRoles={['cliente', 'administrador', 'empleado']}>
                              <Services />
                            </ProtectedRoute>
                        } />

                        <Route path="/admin-dashboard" element={
                            <ProtectedRoute allowedRoles={['administrador']}>
                              <AdminDashboard />
                            </ProtectedRoute>
                        } />

                        <Route path="/gestion-citas" element={
                            <ProtectedRoute allowedRoles={['empleado', 'administrador']}>
                              <div style={{padding: '100px'}}><h2>Gestión de Citas (Empleado)</h2></div>
                            </ProtectedRoute>
                        } />

                        <Route path="/perfil" element={
                            <ProtectedRoute><div style={{padding: '100px'}}><h2>Mi Perfil</h2></div></ProtectedRoute>
                        } />
                    </Routes>
                </main>
                <Footer /> 
                <WhatsAppButton />
            </div>
        </Router>
      </DataProvider>
    </AuthProvider>
  );
}

export default App;
