// src/contexts/DataContext.jsx
import React, { createContext, useContext, useState, useEffect } from 'react';

const DataContext = createContext();

export const useData = () => useContext(DataContext);

export const DataProvider = ({ children }) => {
    // 1. ESTADO DE SERVICIOS
    const [services, setServices] = useState(() => {
        const saved = localStorage.getItem('services');
        return saved ? JSON.parse(saved) : [
            { id: 1, title: 'Estética Completa', description: 'Baño y corte.', price: 450, duration: '2h', category: 'Estética' },
            { id: 2, title: 'Consulta General', description: 'Revisión médica.', price: 300, duration: '45min', category: 'Veterinaria' }
        ];
    });

    // 2. ESTADO DE PRODUCTOS (INVENTARIO)
    const [products, setProducts] = useState(() => {
        const saved = localStorage.getItem('products');
        return saved ? JSON.parse(saved) : [
            { id: 1, name: 'Shampoo Antipulgas', price: 180, stock: 15, category: 'Higiene' }
        ];
    });

    // 3. ESTADO DE CLIENTES (DUEÑOS)
    const [clients, setClients] = useState(() => {
        const saved = localStorage.getItem('clients');
        return saved ? JSON.parse(saved) : [
            { id: 101, name: 'Juan Pérez', phone: '555-0192', email: 'juan@ejemplo.com' }
        ];
    });

    // 4. ESTADO DE MASCOTAS (PACIENTES)
    const [pets, setPets] = useState(() => {
        const saved = localStorage.getItem('pets');
        return saved ? JSON.parse(saved) : [
            { id: 201, petName: 'Firulais', breed: 'Golden Retriever', weight: '30', ownerId: '101', notes: 'Alergia al pollo' }
        ];
    });

    // 5. ESTADO DE VENTAS Y CONTROL (HISTORIAL)
    const [sales, setSales] = useState(() => {
        const saved = localStorage.getItem('sales');
        return saved ? JSON.parse(saved) : [];
    });

    // PERSISTENCIA: Se dispara cada vez que algún estado cambia
    useEffect(() => {
        localStorage.setItem('services', JSON.stringify(services));
        localStorage.setItem('products', JSON.stringify(products));
        localStorage.setItem('clients', JSON.stringify(clients));
        localStorage.setItem('pets', JSON.stringify(pets));
        localStorage.setItem('sales', JSON.stringify(sales));
    }, [services, products, clients, pets, sales]);

    // FUNCIONES DE OPERACIÓN (CRUD BÁSICO)
    const addService = (service) => {
        setServices([...services, { ...service, id: Date.now() }]);
    };

    const addProduct = (product) => {
        setProducts([...products, { ...product, id: Date.now() }]);
    };

    const addClient = (client) => {
        setClients([...clients, { ...client, id: Date.now() }]);
    };

    const addPet = (pet) => {
        setPets([...pets, { ...pet, id: Date.now() }]);
    };

    const addSale = (saleItem, price) => {
        const newSale = {
            id: Date.now(),
            date: new Date().toLocaleDateString(),
            time: new Date().toLocaleTimeString(),
            item: saleItem,
            price: price
        };
        setSales([newSale, ...sales]); // Las ventas más recientes arriba
    };

    // Función para actualizar stock (útil para la pestaña de Control)
    const updateStock = (productId, quantity) => {
        setProducts(products.map(p => 
            p.id === productId ? { ...p, stock: p.stock - quantity } : p
        ));
    };

    return (
        <DataContext.Provider value={{ 
            services, 
            products, 
            pets, 
            clients, 
            sales,
            addService, 
            addProduct,
            addClient,
            addPet,
            addSale,
            updateStock
        }}>
            {children}
        </DataContext.Provider>
    );
};
