// src/App.js
import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import './App.css'; 

import { AuthProvider } from './contexts/AuthContext'; 
import Navbar from './components/Navbar/Navbar'; 
import Footer from './components/Footer/Footer'; 
import WhatsAppButton from './components/WhatsAppButton/WhatsAppButton';
import ProtectedRoute from './components/ProtectedRoute'; 

import Home from './pages/Home';
import Services from './pages/Services'; 
import Login from './components/Login/Login';
import Register from './components/Register/Register';

function App() {
  return (
    <AuthProvider>
        <Router>
            <div className="app-container">
                <Navbar />
                <main className="main-content">
                    <Routes>
                        {/* El login valida la entrada. Si entras a "/", ProtectedRoute te manda a "/acceso" si no hay sesi칩n */}
                        <Route path="/" element={
                            <ProtectedRoute>
                                <Home />
                            </ProtectedRoute>
                        } />

                        <Route path="/acceso" element={<Login />} />
                        <Route path="/registro" element={<Register />} />

                        {/* RUTAS POR ROL */}
                        <Route path="/servicios" element={
                            <ProtectedRoute allowedRoles={['cliente', 'administrador', 'empleado']}>
                              <Services />
                            </ProtectedRoute>
                        } />

                        <Route path="/admin-dashboard" element={
                            <ProtectedRoute allowedRoles={['administrador']}>
                              <div style={{padding: '100px'}}><h2>Panel de Administraci칩n Maestro</h2></div>
                            </ProtectedRoute>
                        } />

                        <Route path="/gestion-citas" element={
                            <ProtectedRoute allowedRoles={['empleado', 'administrador']}>
                              <div style={{padding: '100px'}}><h2>Gesti칩n de Citas (Empleado)</h2></div>
                            </ProtectedRoute>
                        } />

                        <Route path="/perfil" element={
                            <ProtectedRoute>
                              <div style={{padding: '100px'}}><h2>Mi Perfil</h2></div>
                            </ProtectedRoute>
                        } />
                    </Routes>
                </main>
                <Footer /> 
                <WhatsAppButton />
            </div>
        </Router>
    </AuthProvider>
  );
}

export default App;
