import React from 'react';
// Importamos los componentes de react-router-dom
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';

// Componentes de Layout
import Navbar from './components/Navbar/Navbar';
import Footer from './components/Footer/Footer';

// NUEVO: Componentes de Funcionalidad Global
import ParticlesBackground from './components/ParticlesBackground/ParticlesBackground';
import WhatsAppButton from './components/WhatsAppButton/WhatsAppButton'; 

// Componentes de Autenticación (usados como "páginas" por ahora)
import Login from './components/Login/Login';
import Register from './components/Register/Register';

// Páginas Públicas/Comunes
import Home from './pages/Home';
import Shop from './pages/Shop';

// Estilos globales de la aplicación
import './App.css'; 

function App() {
  return (
    <Router>

      {/* 1. FONDO DE PARTÍCULAS: Se renderiza como fondo fijo con z-index bajo */}
      <ParticlesBackground /> 

      <div className="app-container">

        {/* La barra de navegación se renderiza en todas las rutas */}
        <Navbar />

        {/* Contenedor principal donde se renderizan las páginas */}
        <main className="main-content">
          <Routes>

            {/* Rutas Públicas y de Autenticación */}
            <Route path="/" element={<Home />} />
            <Route path="/tienda" element={<Shop />} />
            <Route path="/acceso" element={<Login />} />
            <Route path="/registro" element={<Register />} />

            {/* ---------------------------------------------------- */}
            {/* Rutas Protegidas (se implementarán con componentes de protección) */}
            {/* ---------------------------------------------------- */}

            {/* Ruta Cliente */}
            <Route path="/cliente/agendar" element={<div>Página de Agendamiento Cliente</div>} />

            {/* Ruta Empleado */}
            <Route path="/empleado/agenda" element={<div>Página de Agenda Empleado</div>} />

            {/* Ruta Administrador */}
            <Route path="/admin/dashboard" element={<div>Dashboard de Administración</div>} />

          </Routes>
        </main>

        {/* El pie de página se renderiza en todas las rutas */}
        <Footer />

        {/* 2. BOTÓN DE WHATSAPP: Se renderiza como elemento flotante */}
        <WhatsAppButton phoneNumber="521234567890" message="Hola, me interesa agendar una cita." />

      </div>
    </Router>
  );
}

export default App;
