// src/components/Login/Login.jsx
import React, { useState } from 'react';
import { Link } from 'react-router-dom'; // Para el enlace a Registro
import './Login.css'; // Importamos los estilos específicos de Login

const Login = () => {
    // Estados para almacenar los valores de los campos
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    // Función para manejar el envío del formulario
    const handleSubmit = (e) => {
        e.preventDefault(); // Previene el comportamiento por defecto del formulario (recargar la página)
        console.log('Intento de Login:', { email, password });
        // Aquí iría la lógica de autenticación real (ej. llamada a una API)
        alert(`Login con Email: ${email}`); // Solo para demostración
    };

    return (
        <div className="login-container">
            <div className="login-card">
                <h2>Iniciar Sesión</h2>
                <form onSubmit={handleSubmit}>
                    <div className="input-group">
                        <label htmlFor="email">Email</label>
                        <input
                            type="email"
                            id="email"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            required
                        />
                    </div>
                    <div className="input-group">
                        <label htmlFor="password">Contraseña</label>
                        <input
                            type="password"
                            id="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            required
                        />
                    </div>
                    <button type="submit" className="login-button">
                        Entrar
                    </button>
                </form>
                <div className="login-footer">
                    <p>
                        ¿No tienes una cuenta? <Link to="/registro">Regístrate aquí</Link>
                    </p>
                    <p>
                        <Link to="/recuperar-contrasena">¿Olvidaste tu contraseña?</Link>
                    </p>
                </div>
            </div>
        </div>
    );
};

export default Login;
