// src/components/ParticlesBackground/ParticlesBackground.jsx
import { useCallback } from "react";
import Particles from "react-tsparticles";
import { loadStarsPreset } from "@tsparticles/preset-stars";

const ParticlesBackground = () => {
    // Inicializa el preset de estrellas
    const particlesInit = useCallback(async (engine) => {
        await loadStarsPreset(engine);
    }, []);

    const particlesLoaded = useCallback(async (container) => {
        // console.log(container);
    }, []);

    // Opciones para un efecto de "polvo de estrellas" o "polvo flotante"
    const options = {
        preset: "stars", // Usamos el preset de estrellas
        background: {
            color: {
                value: "#f9f9f9", // Fondo muy claro, casi blanco
            },
        },
        particles: {
            number: {
                value: 80, // Reducimos el número para que sea sutil
            },
            color: {
                value: "#cccccc", // Color del polvo/estrellas, un gris muy claro
            },
            opacity: {
                value: 0.5,
                random: true,
            },
            size: {
                value: 1.5, // Tamaño pequeño
            },
            move: {
                enable: true,
                speed: 0.2, // Movimiento muy lento
                direction: "none",
                random: true,
                straight: false,
                out_mode: "out",
            },
        },
        interactivity: {
            // Desactivamos interacciones para mantener el efecto minimalista
            events: {
                onHover: {
                    enable: false,
                },
                onClick: {
                    enable: false,
                },
            },
        },
    };

    return (
        <Particles
            id="tsparticles"
            init={particlesInit}
            loaded={particlesLoaded}
            options={options}
            style={{
                position: "fixed", // Fijo en el fondo
                top: 0,
                left: 0,
                width: "100%",
                height: "100%",
                zIndex: -1, // Detrás de todo el contenido de la app
            }}
        />
    );
};

export default ParticlesBackground;
