// src/components/Register/Register.jsx
import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom'; // Importamos useNavigate
import './Register.css'; 
import { useAuth } from '../../contexts/AuthContext'; // <-- Importamos useAuth

const Register = () => {
    const [formData, setFormData] = useState({
        name: '',
        email: '',
        password: '',
        confirmPassword: ''
    });
    const [error, setError] = useState(''); // Estado para manejar errores

    // Usamos el hook de autenticación y navegación
    const { register } = useAuth();
    const navigate = useNavigate();

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.id]: e.target.value
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault(); 
        setError('');

        if (formData.password !== formData.confirmPassword) {
            setError("Las contraseñas no coinciden. Por favor, revísalas.");
            return;
        }

        try {
            // Llama a la función de registro del contexto
            const success = register(formData.name, formData.email, formData.password); 

            if (success) {
                // Si el registro es exitoso (y logea automáticamente), redirigimos al Home.
                navigate('/');
            } else {
                // Manejo de error si la función de registro falla
                setError('Error en el registro. Intenta con un email diferente.');
            }
        } catch (err) {
            setError('Ocurrió un error al intentar registrar la cuenta.');
            console.error(err);
        }
    };

    return (
        <div className="register-container">
            <div className="register-card">
                <h2>Crear Cuenta</h2>
                <form onSubmit={handleSubmit}>

                    {/* Mostrar mensaje de error */}
                    {error && <p className="error-message">{error}</p>}

                    <div className="input-group">
                        <label htmlFor="name">Nombre Completo</label>
                        <input
                            type="text"
                            id="name"
                            value={formData.name}
                            onChange={handleChange}
                            required
                        />
                    </div>

                    <div className="input-group">
                        <label htmlFor="email">Email</label>
                        <input
                            type="email"
                            id="email"
                            value={formData.email}
                            onChange={handleChange}
                            required
                        />
                    </div>

                    <div className="input-group">
                        <label htmlFor="password">Contraseña</label>
                        <input
                            type="password"
                            id="password"
                            value={formData.password}
                            onChange={handleChange}
                            required
                        />
                    </div>

                    <div className="input-group">
                        <label htmlFor="confirmPassword">Confirmar Contraseña</label>
                        <input
                            type="password"
                            id="confirmPassword"
                            value={formData.confirmPassword}
                            onChange={handleChange}
                            required
                        />
                    </div>

                    <button type="submit" className="register-button">
                        Registrarse
                    </button>
                </form>
                <div className="register-footer">
                    <p>
                        ¿Ya tienes una cuenta? <Link to="/acceso">Inicia Sesión</Link>
                    </p>
                </div>
            </div>
        </div>
    );
};

export default Register;
