// src/pages/Home.jsx
import React from 'react';
import { Link } from 'react-router-dom';
import './styles.css'; 
import bannerImage from '../../assets/1.jpg'; 

// --- TEMPORAL: Estado de Autenticaci√≥n ---
// Esta variable ser√° reemplazada por el Context/State de autenticaci√≥n real.
// Mientras sea 'false', todos los botones llevan a /acceso.
const isLoggedIn = false; 
// ------------------------------------------

// --- COMPONENTE TARJETA DE SERVICIO ---
const ServiceCard = ({ title, description, icon }) => {
    // Si est√° logeado -> va a /servicios; si no -> va a /acceso
    const linkPath = isLoggedIn ? "/servicios" : "/acceso";

    return (
        <div className="service-card">
            <div className="service-icon">{icon}</div>
            <h3>{title}</h3>
            <p>{description}</p>
            <Link to={linkPath} className="reserve-button service-button">
                Reservar
            </Link>
        </div>
    );
};

// --- NUEVO COMPONENTE TARJETA DE PRODUCTO ---
const ProductCard = ({ title, description, icon }) => {
    // Si est√° logeado -> va a /tienda; si no -> va a /acceso
    const linkPath = isLoggedIn ? "/tienda" : "/acceso";

    return (
        <div className="service-card product-card"> {/* Reutiliza estilos base */}
            <div className="service-icon product-icon">{icon}</div>
            <h3>{title}</h3>
            <p>{description}</p>
            {/* Usamos una clase diferente para diferenciar visualmente el bot√≥n */}
            <Link to={linkPath} className="reserve-button buy-button"> 
                Comprar
            </Link>
        </div>
    );
};
// --------------------------------------------------------------------------


const Home = () => {
    // Rutas condicionales para los botones principales
    const mainReservePath = isLoggedIn ? "/servicios" : "/acceso";
    const shopLinkPath = isLoggedIn ? "/tienda" : "/acceso";

    return (
        <div className="home-page-container" style={{ minHeight: '200vh', paddingBottom: '100px' }}>

            {/* BANNER CAPSULA FLOTANTE */}
            <div 
                className="capsule-banner" 
                style={{ backgroundImage: `url(${bannerImage})` }}
            >
                <h2>El Mejor Cuidado para tu Mascota</h2>
                <p>Grooming, Spa y Salud en un solo lugar.</p>

                {/* Bot√≥n de Reserva -> Usa la ruta condicional */}
                <Link to={mainReservePath} className="reserve-button">
                    Reservar Cita
                </Link>
            </div>

            {/* SECCI√ìN SERVICIOS */}
            <section className="content-section">
                <h3>Nuestros Servicios</h3>

                <div className="service-cards-grid">

                    <ServiceCard 
                        title="Grooming Premium"
                        description="Ba√±o con productos especializados, corte de pelo y u√±as, limpieza de o√≠dos."
                        icon="‚úÇÔ∏è"
                    />

                    <ServiceCard 
                        title="Spa & Relax"
                        description="Masajes terap√©uticos, aromaterapia y tratamientos de hidrataci√≥n para piel y pelo."
                        icon="üõÅ"
                    />

                    <ServiceCard 
                        title="Chequeo B√°sico"
                        description="Revisi√≥n veterinaria r√°pida, vacunas esenciales y control de par√°sitos."
                        icon="ü©∫"
                    />

                </div>

            </section>

            {/* SECCI√ìN PRODUCTOS EXCLUSIVOS */}
            <section className="content-section">
                <h3>Productos Exclusivos</h3>
                <p>Shampoos, acondicionadores y juguetes seleccionados para su bienestar.</p>

                {/* CONTENEDOR DE TARJETAS (Reutiliza la clase grid) */}
                <div className="service-cards-grid">

                    <ProductCard 
                        title="Shampoo Hipoalerg√©nico"
                        description="F√≥rmula suave, ideal para mascotas con piel sensible o alergias."
                        icon="üß¥"
                    />

                    <ProductCard 
                        title="Juguete Interactivo"
                        description="Dise√±ado para la estimulaci√≥n mental y reducir la ansiedad."
                        icon="ü¶¥"
                    />

                    <ProductCard 
                        title="Alimento Premium"
                        description="Dieta balanceada con vitaminas y prote√≠nas esenciales para todas las edades."
                        icon="ü•©"
                    />

                </div>

                {/* Bot√≥n para Ver todo el cat√°logo */}
                <Link to={shopLinkPath} className="reserve-button view-all-button">
                    Ver Cat√°logo Completo
                </Link>
            </section>

            {/* Espacio final */}
            <div style={{ height: '200px' }}></div> 
        </div>
    );
};

export default Home;
