// src/App.js
import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import './App.css'; 

// Importa el proveedor de autenticación
import { AuthProvider } from './contexts/AuthContext'; 

// Componentes de Layout
import Navbar from './components/Navbar/Navbar'; 
import Footer from './components/Footer/Footer'; 
import WhatsAppButton from './components/WhatsAppButton/WhatsAppButton';
import ProtectedRoute from './components/ProtectedRoute'; // <-- Importado para protección

// Páginas
import Home from './pages/Home';
import Services from './pages/Services'; // <-- NUEVA PÁGINA DE SERVICIOS
import Login from './components/Login/Login';
import Register from './components/Register/Register';
// import ParticlesBackground from './components/ParticlesBackground/ParticlesBackground';


function App() {
  return (
    // Envuelve la aplicación con el AuthProvider
    <AuthProvider>
        <Router>

            {/* <ParticlesBackground /> */} 

            <div className="app-container">

                <Navbar />

                <main className="main-content">
                    <Routes>
                        <Route path="/" element={<Home />} />
                        <Route path="/acceso" element={<Login />} />
                        <Route path="/registro" element={<Register />} />

                        {/* RUTAS PROTEGIDAS */}
                        {/* Se usa el ProtectedRoute para envolver el componente Service. 
                           Si el usuario no está logeado, será redirigido a /acceso. */}
                        <Route 
                          path="/servicios" 
                          element={
                            <ProtectedRoute>
                              <Services />
                            </ProtectedRoute>
                          } 
                        />

                        {/* RUTAS FUTURAS PROTEGIDAS */}
                        <Route 
                          path="/tienda" 
                          element={
                            <ProtectedRoute>
                              {/* <Shop /> */} 
                              <h2>Tienda - Ruta Protegida</h2>
                            </ProtectedRoute>
                          } 
                        />
                        <Route 
                          path="/perfil" 
                          element={
                            <ProtectedRoute>
                              {/* <Profile /> */}
                              <h2>Perfil de Usuario - Ruta Protegida</h2>
                            </ProtectedRoute>
                          } 
                        />

                    </Routes>
                </main>

                {/* Nota: Asegúrate de tener los componentes Footer y WhatsAppButton creados */}
                <Footer /> 
                <WhatsAppButton />

            </div>
        </Router>
    </AuthProvider>
  );
}

export default App;
