// src/components/ParticlesBackground/ParticlesBackground.jsx
import { useCallback } from "react";
import Particles from "react-tsparticles";
import { loadStarsPreset } from "@tsparticles/preset-stars";

const ParticlesBackground = () => {

    const particlesInit = useCallback(async (engine) => {
        await loadStarsPreset(engine);
    }, []);

    const particlesLoaded = useCallback(async (container) => {}, []);

    const options = {
        preset: "stars", 
        background: {
            color: {
                // Fondo del lienzo de partículas: Negro muy profundo
                value: "#000000", 
            },
        },
        particles: {
            number: {
                value: 80, 
            },
            color: {
                // Color de las partículas: Blanco sutil (para que contrasten con el fondo negro)
                value: "#ffffff", 
            },
            opacity: {
                value: 0.5,
                random: true,
            },
            size: {
                value: 1.5, 
            },
            move: {
                enable: true,
                speed: 0.2, 
                direction: "none",
                random: true,
                straight: false,
                out_mode: "out",
            },
        },
        interactivity: {
            events: {
                onHover: { enable: false },
                onClick: { enable: false },
            },
        },
    };

    return (
        <Particles
            id="tsparticles"
            init={particlesInit}
            loaded={particlesLoaded}
            options={options}
            style={{
                position: "fixed", 
                top: 0,
                left: 0,
                width: "100%",
                height: "100%",
                zIndex: -1, // ¡Se mantiene detrás!
            }}
        />
    );
};

export default ParticlesBackground;
