// src/components/WhatsAppButton/WhatsAppButton.jsx
import React from 'react';
import './WhatsAppButton.css'; 

// Definición del ícono OFICIAL de WhatsApp (Versión Simplificada y Confiable)
const WhatsAppIcon = () => (
    <svg 
        xmlns="http://www.w3.org/2000/svg" 
        viewBox="0 0 24 24" 
        width="24" 
        height="24" 
        style={{ fill: 'white' }} // Aplicamos el color blanco directamente como estilo en línea
    >
        {/* El path representa el teléfono dentro del logo de WhatsApp */}
        <path d="M12.039 0c-6.627 0-12 5.373-12 12s5.373 12 12 12 12-5.373 12-12-5.373-12-12-12zm.284 5.67c1.884 0 3.514.733 4.545 2.016.142.17.207.389.207.576 0 .445-.333.687-.803.687-.272 0-.544-.125-.717-.374-.633-.873-1.638-1.428-2.732-1.428-2.274 0-4.175 1.838-4.175 4.093 0 .783.255 1.488.683 2.05.076.096.155.187.237.275l.076.082.029.026c.205.216 1.766 1.77 1.861 1.866.19.19.43.275.67.275.333 0 .63-.125.852-.338.45-.45.928-1.01 1.407-1.467.272-.25.568-.458.892-.619.404-.19.865-.262 1.353-.262 1.258 0 2.276 1.018 2.276 2.276 0 .86-.48 1.63-1.26 2.062-.236.12-.48.18-.72.18-.284 0-.585-.102-.857-.306-.632-.472-1.39-1.077-2.275-1.74-1.284-1.1-2.482-2.583-3.037-4.238-.15-.452-.225-.92-.225-1.35 0-3.35 2.76-6.108 6.136-6.108z"/>
    </svg>
);


const WhatsAppButton = ({ phoneNumber = "+521234567890", message = "Hola, me interesa agendar una cita de estética para mi mascota." }) => {
    const url = `https://wa.me/${phoneNumber}?text=${encodeURIComponent(message)}`;

    return (
        <a 
            href={url}
            target="_blank"
            rel="noopener noreferrer"
            className="whatsapp-button"
            aria-label="Contactar por WhatsApp"
        >
            <WhatsAppIcon />
        </a>
    );
};

export default WhatsAppButton;
