// src/components/Register/Register.jsx
import React, { useState } from 'react';
import { Link } from 'react-router-dom'; 
import './Register.css'; 

const Register = () => {
    // Estados para almacenar los valores del formulario
    const [formData, setFormData] = useState({
        name: '',
        email: '',
        password: '',
        confirmPassword: ''
    });

    // Manejador genérico de cambios en los inputs
    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.id]: e.target.value
        });
    };

    // Función para manejar el envío del formulario
    const handleSubmit = (e) => {
        e.preventDefault(); 

        // Verificación básica de que las contraseñas coincidan
        if (formData.password !== formData.confirmPassword) {
            alert("Las contraseñas no coinciden. Por favor, revísalas.");
            return;
        }

        console.log('Datos de Registro:', formData);
        // Aquí iría la lógica de registro (ej. llamada a una API)
        alert(`Registro exitoso para: ${formData.name}`); // Solo para demostración

        // Limpiar el formulario después del envío (opcional)
        setFormData({
            name: '',
            email: '',
            password: '',
            confirmPassword: ''
        });
    };

    return (
        <div className="register-container">
            <div className="register-card">
                <h2>Crear Cuenta</h2>
                <form onSubmit={handleSubmit}>

                    {/* Nombre */}
                    <div className="input-group">
                        <label htmlFor="name">Nombre Completo</label>
                        <input
                            type="text"
                            id="name"
                            value={formData.name}
                            onChange={handleChange}
                            required
                        />
                    </div>

                    {/* Email */}
                    <div className="input-group">
                        <label htmlFor="email">Email</label>
                        <input
                            type="email"
                            id="email"
                            value={formData.email}
                            onChange={handleChange}
                            required
                        />
                    </div>

                    {/* Contraseña */}
                    <div className="input-group">
                        <label htmlFor="password">Contraseña</label>
                        <input
                            type="password"
                            id="password"
                            value={formData.password}
                            onChange={handleChange}
                            required
                        />
                    </div>

                    {/* Confirmar Contraseña */}
                    <div className="input-group">
                        <label htmlFor="confirmPassword">Confirmar Contraseña</label>
                        <input
                            type="password"
                            id="confirmPassword"
                            value={formData.confirmPassword}
                            onChange={handleChange}
                            required
                        />
                    </div>

                    <button type="submit" className="register-button">
                        Registrarse
                    </button>
                </form>
                <div className="register-footer">
                    <p>
                        ¿Ya tienes una cuenta? <Link to="/acceso">Inicia Sesión</Link>
                    </p>
                </div>
            </div>
        </div>
    );
};

export default Register;
