// src/contexts/DataContext.jsx
import React, { createContext, useContext, useState, useEffect } from 'react';

const DataContext = createContext();

export const useData = () => useContext(DataContext);

export const DataProvider = ({ children }) => {
    // Estado para Servicios
    const [services, setServices] = useState(() => {
        const saved = localStorage.getItem('services');
        return saved ? JSON.parse(saved) : [
            { id: 1, title: 'Estética Completa', description: 'Baño, corte y drenado de glándulas.', price: 450, duration: '2h', category: 'Estética' },
            { id: 2, title: 'Consulta Veterinaria', description: 'Revisión general de salud.', price: 300, duration: '45min', category: 'Veterinaria' }
        ];
    });

    // Estado para Productos
    const [products, setProducts] = useState(() => {
        const saved = localStorage.getItem('products');
        return saved ? JSON.parse(saved) : [
            { id: 1, name: 'Shampoo Antipulgas', price: 180, stock: 15, category: 'Higiene' }
        ];
    });

    // Estado para Mascotas (Pacientes)
    const [pets, setPets] = useState(() => {
        const saved = localStorage.getItem('pets');
        return saved ? JSON.parse(saved) : [];
    });

    // Persistencia en LocalStorage
    useEffect(() => {
        localStorage.setItem('services', JSON.stringify(services));
        localStorage.setItem('products', JSON.stringify(products));
        localStorage.setItem('pets', JSON.stringify(pets));
    }, [services, products, pets]);

    const addService = (service) => {
        setServices([...services, { ...service, id: Date.now() }]);
    };

    const addProduct = (product) => {
        setProducts([...products, { ...product, id: Date.now() }]);
    };

    return (
        <DataContext.Provider value={{ services, products, pets, addService, addProduct }}>
            {children}
        </DataContext.Provider>
    );
};
