// src/components/Login/Login.jsx
import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom'; // Importamos useNavigate
import './Login.css'; 
import { useAuth } from '../../contexts/AuthContext'; // <-- Importamos useAuth

const Login = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState(''); // Estado para manejar errores

    // Usamos el hook de autenticación y navegación
    const { login } = useAuth();
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault(); 
        setError(''); // Limpiamos errores anteriores

        try {
            // Llama a la función de login del contexto
            const success = login(email, password); 

            if (success) {
                // Redirigimos al usuario a la página de inicio
                navigate('/');
            } else {
                // Mensaje de error si la simulación falla
                setError('Email o contraseña incorrectos. Intenta con test@mascotas.com / 123456');
            }
        } catch (err) {
            setError('Ocurrió un error durante el inicio de sesión.');
            console.error(err);
        }
    };

    return (
        <div className="login-container">
            <div className="login-card">
                <h2>Iniciar Sesión</h2>
                <form onSubmit={handleSubmit}>

                    {/* Mostrar mensaje de error */}
                    {error && <p className="error-message">{error}</p>} 

                    <div className="input-group">
                        <label htmlFor="email">Email</label>
                        <input
                            type="email"
                            id="email"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            required
                        />
                    </div>
                    <div className="input-group">
                        <label htmlFor="password">Contraseña</label>
                        <input
                            type="password"
                            id="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            required
                        />
                    </div>
                    <button type="submit" className="login-button">
                        Entrar
                    </button>
                </form>
                <div className="login-footer">
                    <p>
                        ¿No tienes una cuenta? <Link to="/registro">Regístrate aquí</Link>
                    </p>
                    <p>
                        <Link to="/recuperar-contrasena">¿Olvidaste tu contraseña?</Link>
                    </p>
                </div>
            </div>
        </div>
    );
};

export default Login;
