import React, { useState, useMemo } from 'react';
import { useData } from '../../contexts/DataContext';
import { useAuth } from '../../contexts/AuthContext';
import { 
    FaCut, FaShoppingBag, FaPaw, FaChevronLeft, FaChevronRight, 
    FaPlusCircle, FaSignOutAlt, FaUserShield, FaUsers, FaChartLine 
} from 'react-icons/fa';
import './AdminDashboard.css';

const AdminDashboard = () => {
    const { services, products, pets, clients, sales, addService, addProduct, addPet, addClient } = useData();
    const { logout, user } = useAuth();
    const [tab, setTab] = useState('control');
    const [isSidebarOpen, setIsSidebarOpen] = useState(true);

    // Estados para formularios
    const [serviceForm, setServiceForm] = useState({ title: '', description: '', price: '', duration: '', category: 'Estética' });
    const [productForm, setProductForm] = useState({ name: '', price: '', stock: '', category: 'Alimentos' });
    const [clientForm, setClientForm] = useState({ name: '', phone: '', email: '' });
    const [petForm, setPetForm] = useState({ petName: '', breed: '', weight: '', ownerId: '', notes: '' });

    // LÓGICA DE CONTROL (Métricas)
    const stats = useMemo(() => {
        const totalSales = sales.reduce((acc, sale) => acc + Number(sale.price), 0);
        const lowStock = products.filter(p => p.stock < 5).length;
        return { totalSales, lowStock, totalPets: pets.length, totalClients: clients.length };
    }, [sales, products, pets, clients]);

    const handleSave = (type, e) => {
        e.preventDefault();
        if (type === 'service') { 
            addService(serviceForm); 
            setServiceForm({ title: '', description: '', price: '', duration: '', category: 'Estética' }); 
        }
        if (type === 'product') { 
            addProduct(productForm); 
            setProductForm({ name: '', price: '', stock: '', category: 'Alimentos' }); 
        }
        if (type === 'client') { 
            addClient(clientForm); 
            setClientForm({ name: '', phone: '', email: '' }); 
        }
        if (type === 'pet') { 
            addPet(petForm); 
            setPetForm({ petName: '', breed: '', weight: '', ownerId: '', notes: '' }); 
        }
    };

    return (
        <div className={`admin-layout ${isSidebarOpen ? 'sidebar-open' : 'sidebar-closed'}`}>
            {/* TOP BAR */}
            <header className="admin-top-bar">
                <div className="admin-user-info"><FaUserShield /> <span>Admin: <strong>{user?.name}</strong></span></div>
                <button className="logout-pill" onClick={logout}><FaSignOutAlt /> <span>Cerrar Sesión</span></button>
            </header>

            {/* SIDEBAR */}
            <aside className="admin-sidebar">
                <button className="toggle-btn" onClick={() => setIsSidebarOpen(!isSidebarOpen)}>
                    {isSidebarOpen ? <FaChevronLeft /> : <FaChevronRight />}
                </button>
                <div className="sidebar-header">{isSidebarOpen && <h3>VetControl Pro</h3>}</div>
                <nav className="sidebar-nav">
                    <button onClick={() => setTab('control')} className={`nav-capsule ${tab === 'control' ? 'active' : ''}`}><FaChartLine /> {isSidebarOpen && <span>Control</span>}</button>
                    <button onClick={() => setTab('clientes')} className={`nav-capsule ${tab === 'clientes' ? 'active' : ''}`}><FaUsers /> {isSidebarOpen && <span>Clientes</span>}</button>
                    <button onClick={() => setTab('pacientes')} className={`nav-capsule ${tab === 'pacientes' ? 'active' : ''}`}><FaPaw /> {isSidebarOpen && <span>Pacientes</span>}</button>
                    <button onClick={() => setTab('servicios')} className={`nav-capsule ${tab === 'servicios' ? 'active' : ''}`}><FaCut /> {isSidebarOpen && <span>Servicios</span>}</button>
                    <button onClick={() => setTab('productos')} className={`nav-capsule ${tab === 'productos' ? 'active' : ''}`}><FaShoppingBag /> {isSidebarOpen && <span>Productos</span>}</button>
                </nav>
            </aside>

            {/* MAIN PANEL */}
            <main className="admin-main-panel">
                <header className="content-header">
                    <h2>{tab === 'control' ? 'Dashboard de Control' : `Gestión de ${tab}`}</h2>
                </header>

                <div className="content-body">

                    {/* TAB: CONTROL / DASHBOARD */}
                    {tab === 'control' && (
                        <div className="control-grid fade-in">
                            <div className="stat-card"><h3>${stats.totalSales}</h3><p>Ventas Totales</p></div>
                            <div className="stat-card"><h3>{stats.totalPets}</h3><p>Pacientes</p></div>
                            <div className="stat-card"><h3>{stats.totalClients}</h3><p>Clientes</p></div>
                            <div className="stat-card warning"><h3>{stats.lowStock}</h3><p>Stock Bajo</p></div>

                            <div className="full-width-table">
                                <h4>Últimos Movimientos de Venta</h4>
                                <table>
                                    <thead><tr><th>Fecha</th><th>Concepto</th><th>Monto</th></tr></thead>
                                    <tbody>
                                        {sales.map(s => <tr key={s.id}><td>{s.date}</td><td>{s.item}</td><td>${s.price}</td></tr>)}
                                    </tbody>
                                </table>
                            </div>
                        </div>
                    )}

                    {/* TAB: CLIENTES */}
                    {tab === 'clientes' && (
                        <div className="fade-in">
                            <form onSubmit={(e) => handleSave('client', e)} className="dashboard-form">
                                <div className="form-header"><FaPlusCircle /> <h4>Registrar Nuevo Cliente</h4></div>
                                <div className="input-grid">
                                    <input type="text" placeholder="Nombre Completo" value={clientForm.name} onChange={e => setClientForm({...clientForm, name: e.target.value})} required />
                                    <input type="text" placeholder="Teléfono" value={clientForm.phone} onChange={e => setClientForm({...clientForm, phone: e.target.value})} required />
                                    <input type="email" placeholder="Correo" value={clientForm.email} onChange={e => setClientForm({...clientForm, email: e.target.value})} required />
                                </div>
                                <button type="submit" className="main-action-btn">Guardar Cliente</button>
                            </form>
                            <div className="data-table-container">
                                <table>
                                    <thead><tr><th>Nombre</th><th>Teléfono</th><th>Email</th></tr></thead>
                                    <tbody>
                                        {clients.map(c => <tr key={c.id}><td>{c.name}</td><td>{c.phone}</td><td>{c.email}</td></tr>)}
                                    </tbody>
                                </table>
                            </div>
                        </div>
                    )}

                    {/* TAB: PACIENTES */}
                    {tab === 'pacientes' && (
                        <div className="fade-in">
                            <form onSubmit={(e) => handleSave('pet', e)} className="dashboard-form">
                                <div className="form-header"><FaPlusCircle /> <h4>Registrar Nueva Mascota</h4></div>
                                <div className="input-grid">
                                    <input type="text" placeholder="Nombre Mascota" value={petForm.petName} onChange={e => setPetForm({...petForm, petName: e.target.value})} required />
                                    <input type="text" placeholder="Raza" value={petForm.breed} onChange={e => setPetForm({...petForm, breed: e.target.value})} required />
                                    <select value={petForm.ownerId} onChange={e => setPetForm({...petForm, ownerId: e.target.value})} required>
                                        <option value="">Asignar Dueño...</option>
                                        {clients.map(c => <option key={c.id} value={c.id}>{c.name}</option>)}
                                    </select>
                                    <textarea placeholder="Notas médicas" value={petForm.notes} onChange={e => setPetForm({...petForm, notes: e.target.value})} />
                                </div>
                                <button type="submit" className="main-action-btn">Guardar Mascota</button>
                            </form>
                            <div className="patient-gallery">
                                {pets.map(p => (
                                    <div key={p.id} className="pet-capsule-card">
                                        <div className="pet-icon"><FaPaw /></div>
                                        <div className="pet-info">
                                            <h4>{p.petName}</h4>
                                            <p>{p.breed} • {clients.find(c => String(c.id) === String(p.ownerId))?.name || 'S/D'}</p>
                                        </div>
                                    </div>
                                ))}
                            </div>
                        </div>
                    )}

                    {/* TAB: SERVICIOS */}
                    {tab === 'servicios' && (
                        <div className="fade-in">
                            <form onSubmit={(e) => handleSave('service', e)} className="dashboard-form">
                                <div className="form-header"><FaPlusCircle /> <h4>Nuevo Servicio</h4></div>
                                <div className="input-grid">
                                    <input type="text" placeholder="Título" value={serviceForm.title} onChange={e => setServiceForm({...serviceForm, title: e.target.value})} required />
                                    <input type="number" placeholder="Precio ($)" value={serviceForm.price} onChange={e => setServiceForm({...serviceForm, price: e.target.value})} required />
                                    <input type="text" placeholder="Duración" value={serviceForm.duration} onChange={e => setServiceForm({...serviceForm, duration: e.target.value})} required />
                                </div>
                                <button type="submit" className="main-action-btn">Crear Servicio</button>
                            </form>
                            <div className="data-table-container">
                                <table>
                                    <thead><tr><th>Servicio</th><th>Precio</th><th>Duración</th></tr></thead>
                                    <tbody>
                                        {services.map(s => <tr key={s.id}><td>{s.title}</td><td>${s.price}</td><td>{s.duration}</td></tr>)}
                                    </tbody>
                                </table>
                            </div>
                        </div>
                    )}

                    {/* TAB: PRODUCTOS */}
                    {tab === 'productos' && (
                        <div className="fade-in">
                            <form onSubmit={(e) => handleSave('product', e)} className="dashboard-form">
                                <div className="form-header"><FaPlusCircle /> <h4>Ingresar Producto al Inventario</h4></div>
                                <div className="input-grid">
                                    <input type="text" placeholder="Nombre del Producto" value={productForm.name} onChange={e => setProductForm({...productForm, name: e.target.value})} required />
                                    <input type="number" placeholder="Precio Venta" value={productForm.price} onChange={e => setProductForm({...productForm, price: e.target.value})} required />
                                    <input type="number" placeholder="Stock Inicial" value={productForm.stock} onChange={e => setProductForm({...productForm, stock: e.target.value})} required />
                                </div>
                                <button type="submit" className="main-action-btn">Guardar en Almacén</button>
                            </form>
                            <div className="data-table-container">
                                <table>
                                    <thead><tr><th>Producto</th><th>Precio</th><th>Stock</th><th>Estado</th></tr></thead>
                                    <tbody>
                                        {products.map(p => (
                                            <tr key={p.id}>
                                                <td>{p.name}</td>
                                                <td>${p.price}</td>
                                                <td>{p.stock}</td>
                                                <td>{p.stock < 5 ? <span style={{color: 'red'}}>Bajo Stock</span> : 'Ok'}</td>
                                            </tr>
                                        ))}
                                    </tbody>
                                </table>
                            </div>
                        </div>
                    )}

                </div>
            </main>
        </div>
    );
};

export default AdminDashboard;
