// src/pages/Home.jsx
import React from 'react';
import { Link } from 'react-router-dom';
import './styles.css'; // Importamos los estilos del banner

const Home = () => {
  return (
    // La clase home-page-container puede usarse para estilos generales de la página
    <div className="home-page-container" style={{ minHeight: '200vh', paddingBottom: '100px' }}>

      {/* BANNER CAPSULA FLOTANTE */}
      <div className="capsule-banner">
        <h2>¡Tu Mascota Merece lo Mejor!</h2>
        <p>Servicios de estética y bienestar de primera calidad, diseñados con amor y profesionalismo.</p>

        {/* Botón de Reserva -> Lleva al Login/Registro */}
        <Link to="/acceso" className="reserve-button">
          Reservar Cita / Acceder
        </Link>
      </div>

      {/* SECCIONES DE CONTENIDO (para scroll) */}
      <section className="content-section">
        <h3>Nuestros Servicios</h3>
        <p>Descubre el grooming, spa y tratamientos de salud que ofrecemos.</p>
        <div style={{ height: '300px' }}></div>
      </section>

      <section className="content-section">
        <h3>Productos Exclusivos</h3>
        <p>Shampoos, acondicionadores y juguetes seleccionados para su bienestar.</p>
        <div style={{ height: '400px' }}></div>
      </section>

      {/* Espacio final */}
      <div style={{ height: '200px' }}></div> 
    </div>
  );
};

export default Home;
