import React, { useState, useEffect } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom'; // <-- ¡Importamos useLocation y useNavigate!
import './Navbar.css';

// --- ICONOS ---
const MenuIcon = () => (
    <svg xmlns="http://www.w3.org/2000/svg" height="24" viewBox="0 0 24 24" width="24" fill="currentColor">
        <path d="M0 0h24v24H0z" fill="none"/>
        <path d="M3 18h18v-2H3v2zm0-5h18v-2H3v2zm0-7v2h18V6H3z"/>
    </svg>
);
const UserIcon = () => (
    <svg xmlns="http://www.w3.org/2000/svg" height="24" viewBox="0 0 24 24" width="24" fill="currentColor">
        <path d="M0 0h24v24H0z" fill="none"/>
        <path d="M12 12c2.21 0 4-1.79 4-4s-1.79-4-4-4-4 1.79-4 4 1.79 4 4 4zm0 2c-2.67 0-8 1.34-8 4v2h16v-2c0-2.66-5.33-4-8-4z"/>
    </svg>
);
// --------------------------------------------------------

const Navbar = () => {
  // Hooks de React Router DOM
  const location = useLocation(); // Nos dice la ruta actual
  const navigate = useNavigate(); // Nos permite navegar programáticamente

  const [scrolled, setScrolled] = useState(false);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  // Lógica de Scroll (sin cambios)
  const handleScroll = () => {
    setScrolled(window.scrollY > 100); 
  };

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []); 

  // Función CLAVE: Maneja el clic en "Inicio" o el Logo
  const handleHomeClick = (e) => {
    e.preventDefault(); // Detenemos el comportamiento predeterminado del Link

    closeMobileMenu();

    if (location.pathname === '/') {
      // 1. SI ESTAMOS EN LA PÁGINA DE INICIO: Hacemos scroll al inicio
      window.scrollTo({
        top: 0,
        behavior: 'smooth' // Scroll suave
      });
    } else {
      // 2. SI ESTAMOS EN OTRA PÁGINA: Navegamos a la raíz y luego hacemos scroll
      navigate('/');

      // Usamos setTimeout para asegurar que el scroll se ejecuta después de la navegación
      setTimeout(() => {
        window.scrollTo({
            top: 0,
            behavior: 'smooth'
        });
      }, 50); // Pequeño retraso
    }
  };

  const navbarClasses = `navbar-container ${scrolled ? 'scrolled-capsule' : 'top-transparent'} ${isMobileMenuOpen ? 'menu-open' : ''}`;

  const closeMobileMenu = () => {
    if (isMobileMenuOpen) {
      setIsMobileMenuOpen(false);
    }
  };

  return (
    <header className={navbarClasses}>
      <div className="navbar-content">
        {/* Logo/Nombre de la Marca -> USA handleHomeClick */}
        <Link to="/" className="navbar-logo" onClick={handleHomeClick}>
          MASCOTAS
        </Link>

        {/* Links de Navegación (Solo Desktop) */}
        <nav className={`nav-links-desktop`}>
          {/* Enlace "Inicio" -> USA handleHomeClick */}
          <Link to="/" className="nav-item" onClick={handleHomeClick}>Inicio</Link> 
          <Link to="/servicios" className="nav-item">Servicios</Link>
          <Link to="/tienda" className="nav-item">Tienda</Link>
          <Link to="/contacto" className="nav-item">Contacto</Link>
        </nav>

        {/* Botones de Acción (Perfil/Menú) */}
        <div className="navbar-actions">
          {/* Ícono de Perfil/Login -> Mantiene la navegación normal */}
          <Link to="/acceso" className="nav-icon" title="Iniciar Sesión" onClick={closeMobileMenu}>
            <UserIcon />
          </Link>

          {/* Botón de Menú Móvil (Solo Mobile) */}
          <button 
            className="mobile-menu-toggle" 
            onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
            aria-label="Toggle Navigation"
          >
            <MenuIcon />
          </button>
        </div>
      </div>

      {/* Menú Desplegable Móvil */}
      <nav className={`nav-links-mobile ${isMobileMenuOpen ? 'open' : ''}`}>
        {/* Enlace "Inicio" Móvil -> USA handleHomeClick */}
        <Link to="/" className="nav-item" onClick={handleHomeClick}>Inicio</Link>
        <Link to="/servicios" className="nav-item" onClick={closeMobileMenu}>Servicios</Link>
        <Link to="/tienda" className="nav-item" onClick={closeMobileMenu}>Tienda</Link>
        <Link to="/contacto" className="nav-item" onClick={closeMobileMenu}>Contacto</Link>
        <Link to="/acceso" className="nav-item access-link" onClick={closeMobileMenu}>Iniciar Sesión</Link>
      </nav>
    </header>
  );
};

export default Navbar;
